input = ""

cost = {
    "A": 0,
    "B": 1,
    "C": 3,
    "D": 5,
    "x": 0
}

total = 0

# Iterate by pairs of characters
for i in range(0, len(input)-1, 2):
    a1 = input[i]
    a2 = input[i+1]

    if a1 == "x":
        total += cost[a2]
    elif a2 == "x":
        total += cost[a1]
    else:
        total += cost[a1] + cost[a2] + 2

print(total)
