input = ""

cost = {
    "A": 0,
    "B": 1,
    "C": 3,
    "D": 5,
    "x": 0
}

total = 0

# Iterate by pairs of characters
for i in range(0, len(input)-2, 3):
    chars = input[i:i+3]
    print(f"chars: {chars}")

    # Count number of x's
    x_count = chars.count("x")
    print(f"chars: {chars}, x_count: {x_count}")

    if x_count == 0:
        total += cost[chars[0]] + cost[chars[1]] + cost[chars[2]] + 6
    elif x_count == 1:
        total += cost[chars[0]] + cost[chars[1]] + cost[chars[2]] + 2
    elif x_count == 2:
        total += cost[chars[0]] + cost[chars[1]] + cost[chars[2]]


print(total)
